<?php session_start(); if(!isset($_SESSION["username"])){header("Location: ../login.php"); exit();}$dbhost = "localhost"; $dbuser = "root"; $dbpass = ""; $dbname = "atom";date_default_timezone_set("America/Denver");$backup_file = $dbname."-".date("Y-m-d")."-".date("H:i:s") . ".sql";$command = "mysqldump --opt -h $dbhost -u $dbuser -p$dbpass $dbname > $backup_file";if(system($command) !== FALSE) {$message = "<p class='alert alert-success'>Database backup complete!</p>"; header("Location: ../index.php?id=".$message."");}else {$message = "<p class='alert alert-warning'>Database backup failed!</p>"; header("Location: ../index.php?id=".$message."");}?>